import { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Login from './pages/login'
import Home from './pages/home'

class App extends Component {
    constructor() {
        super()
        // this.state = {
        //     isLogin: localStorage.getItem('isLogin'),
        // }
    }

    componentDidMount() {
        localStorage.setItem('isLogin', localStorage.hasOwnProperty('isLogin')? localStorage.getItem('isLogin'): false)
    }

    render() {
        console.log(localStorage.getItem('isLogin'));
        return (
            <>
                <Router>
                    <Switch>
                        <Route exact path='/' render={() => localStorage.getItem('isLogin') ? <Redirect to={`/home`} /> : <Login/>} />
                        <Route path='/home' render={() => localStorage.getItem('isLogin') ? <Home setState={this.setState.bind(this)} /> : <Redirect to={`/`} />} />
                    </Switch>
                </Router>
            </>
        )
    }
}

export default App
