import { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Login from './pages/login'
import Home from './pages/home'

class App extends Component {
    constructor() {
        super()
        this.state = {
            isLogin: localStorage.getItem('isLogin'),
        }
    }

    componentDidMount() {
        localStorage.setItem('isLogin', this.state.isLogin)
    }

    render() {
        console.log(localStorage.getItem('isLogin'));
        return (
            <>
                <Router>
                    <Switch>
                        <Route exact path='/' render={() => localStorage.getItem('isLogin') ? <Redirect to={`/home`} /> : <Login setState={this.setState.bind(this)} />} />
                        <Route path='/home' render={() => localStorage.getItem('isLogin')==='null' ? <Home setState={this.setState.bind(this)} /> : <Redirect to={`/`} />} />
                    </Switch>
                </Router>
            </>
        )
    }
}

export default App
