import { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Login from './pages/login'
import Home from './pages/home'

class App extends Component {
    constructor() {
        super()
    }

    componentDidMount() {
        this.state.isLogin
        localStorage.setItem('isLogin', false)
    }

    state = {
        isLogin: false,
    }

    render() {
        return (
            <>
                <Router>
                    <Switch>
                        <Route exact path='/' render={() => this.state.isLogin && localStorage.getItem('isLogin') ? <Redirect to={`/home`} /> : <Login setState={this.setState.bind(this)} />} />
                        <Route path='/home' render={() => this.state.isLogin || (isFinite && localStorage) ? <Home setState={this.setState.bind(this)} /> : <Redirect to={`/`} />} />
                    </Switch>
                </Router>
            </>
        )
    }
}

export default App
