import { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Login from './pages/login'
import Home from './pages/home'

class App extends Component
{
    constructor()
    {
        super()
    }
    
    componentDidMount()
    {
        localStorage.setItem('isLogin',false)
    }

    state={
        isLogin:false,
    }

    render()
    {
        // const isLogin=localStorage.getItem('isLocal')
        return(
            <>
            <Router>
                <Switch>
                    <Route exact path='/' render={()=>this.state.isLogin? <Redirect to={`/home`}/>: <Login setState={this.set}/>}/>
                    {/* <Route exact path='/' render={()=>this.state.isLogin? <Redirect to='/home'/>: <Login/>}/> */}
                    <Route path='/home' render={()=>this.state.isLogin? <Home/>: <Redirect to={`/`}/>}/>
                </Switch>
            </Router>
            </>
        )
    }
}

export default App
